	(4, 'category.show', '/forum/categories/*', '{category_name} - Thảo luận Kỹ thuật | MechaMap', 'Các chủ đề thảo luận trong danh mục {category_name}. Tham gia cộng đồng kỹ sư cơ khí để học hỏi và chia sẻ kinh nghiệm.', '{category_name}, forum cơ khí, thảo luận kỹ thuật, cộng đồng kỹ sư', '{category_name} - Forum MechaMap', 'Thảo luận về {category_name} trong cộng đồng kỹ sư cơ khí Việt Nam.', NULL, NULL, NULL, NULL, '/forum/categories/{category_slug}', 0, NULL, 1, '2025-06-25 10:57:14', '2025-06-25 10:57:14'),
	(5, 'user.profile', '/users/*', 'Hồ sơ {user_name} | MechaMap', 'Xem hồ sơ và hoạt động của {user_name} trong cộng đồng MechaMap.', 'hồ sơ người dùng, {user_name}, cộng đồng kỹ sư', NULL, NULL, NULL, NULL, NULL, NULL, '/users/{user_id}', 1, NULL, 1, '2025-06-25 10:57:14', '2025-06-25 10:57:14'),
