INSERT INTO `knowledge_articles` VALUES (1,'Hướng Dẫn Thiết Kế Bánh Răng Cơ Bản','huong-dan-thiet-ke-banh-rang-co-ban','Tìm hiểu các nguyên lý cơ bản trong thiết kế bánh răng, từ tính toán mô-đun đến chọn vật liệu phù hợp.','<h2>Giới thiệu về Bánh Răng</h2><p>Bánh răng là một trong những chi tiết máy quan trọng nhất trong cơ khí, được sử dụng để truyền chuyển động và lực giữa các trục.</p><h3>Các Loại Bánh Răng</h3><ul><li>Bánh răng trụ răng thẳng</li><li>Bánh răng trụ răng nghiêng</li><li>Bánh răng côn</li><li>Bánh răng vít</li></ul><h3>Tính Toán Thiết Kế</h3><p>Để thiết kế bánh răng, cần tính toán các thông số sau:</p><ol><li>Mô-đun (m)</li><li>Số răng (z)</li><li>Đường kính chia (d)</li><li>Chiều rộng răng (b)</li></ol>',NULL,'tutorial',NULL,NULL,NULL,'beginner',NULL,NULL,NULL,NULL,54,NULL,'published','2025-06-26 05:01:28',NULL,0,4.50,12,1,0,'2025-07-01 05:01:28','2025-07-01 05:01:28'),(2,'Phân Tích Ứng Suất Trong Sức Bền Vật Liệu','phan-tich-ung-suat-trong-suc-ben-vat-lieu','Hướng dẫn chi tiết về cách phân tích ứng suất và biến dạng trong các chi tiết máy.','<h2>Khái Niệm Ứng Suất</h2><p>Ứng suất là đại lượng đặc trưng cho cường độ của lực nội tại một điểm trong vật thể.</p><h3>Các Loại Ứng Suất</h3><ul><li>Ứng suất pháp tuyến</li><li>Ứng suất tiếp tuyến</li><li>Ứng suất tương đương</li></ul>',NULL,'tutorial',NULL,NULL,NULL,'intermediate',NULL,NULL,NULL,NULL,54,NULL,'published','2025-06-28 05:01:28',NULL,0,4.20,8,0,0,'2025-07-01 05:01:28','2025-07-01 05:01:28'),(3,'Lập Trình PLC Siemens S7-1200','lap-trinh-plc-siemens-s7-1200','Hướng dẫn từ cơ bản đến nâng cao về lập trình PLC Siemens S7-1200.','<h2>Giới Thiệu PLC S7-1200</h2><p>PLC S7-1200 là dòng PLC compact của Siemens, phù hợp cho các ứng dụng tự động hóa vừa và nhỏ.</p>',NULL,'tutorial',NULL,NULL,NULL,'advanced',NULL,NULL,NULL,NULL,54,NULL,'draft',NULL,NULL,0,0.00,0,0,0,'2025-07-01 05:01:28','2025-07-01 05:01:28');
