DROP TABLE IF EXISTS `marketplace_products`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `marketplace_products` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `uuid` char(36) NOT NULL,
  `name` varchar(191) NOT NULL,
  `slug` varchar(191) NOT NULL,
  `description` text NOT NULL,
  `short_description` text DEFAULT NULL,
  `sku` varchar(191) NOT NULL,
  `seller_id` bigint(20) unsigned NOT NULL,
  `product_category_id` bigint(20) unsigned DEFAULT NULL,
  `product_type` enum('digital','new_product','used_product') NOT NULL DEFAULT 'new_product',
  `seller_type` enum('supplier','manufacturer','brand') NOT NULL DEFAULT 'supplier',
  `industry_category` varchar(191) DEFAULT NULL,
  `price` decimal(12,2) NOT NULL,
  `sale_price` decimal(12,2) DEFAULT NULL,
  `is_on_sale` tinyint(1) NOT NULL DEFAULT 0,
  `sale_starts_at` timestamp NULL DEFAULT NULL,
  `sale_ends_at` timestamp NULL DEFAULT NULL,
  `stock_quantity` int(11) NOT NULL DEFAULT 0,
  `manage_stock` tinyint(1) NOT NULL DEFAULT 1,
  `in_stock` tinyint(1) NOT NULL DEFAULT 1,
  `low_stock_threshold` int(11) NOT NULL DEFAULT 5,
  `technical_specs` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin CHECK (json_valid(`technical_specs`)),
  `mechanical_properties` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin CHECK (json_valid(`mechanical_properties`)),
  `material` varchar(191) DEFAULT NULL,
  `manufacturing_process` varchar(191) DEFAULT NULL,
  `standards_compliance` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin CHECK (json_valid(`standards_compliance`)),
  `file_formats` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin CHECK (json_valid(`file_formats`)),
  `software_compatibility` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin CHECK (json_valid(`software_compatibility`)),
  `file_size_mb` decimal(8,2) DEFAULT NULL,
  `download_limit` int(11) DEFAULT NULL,
  `digital_files` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin CHECK (json_valid(`digital_files`)),
  `images` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin CHECK (json_valid(`images`)),
  `featured_image` varchar(191) DEFAULT NULL,
  `attachments` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin CHECK (json_valid(`attachments`)),
  `meta_title` varchar(191) DEFAULT NULL,
  `meta_description` text DEFAULT NULL,
  `tags` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin CHECK (json_valid(`tags`)),
  `status` enum('draft','pending','approved','rejected','suspended') NOT NULL DEFAULT 'draft',
  `is_featured` tinyint(1) NOT NULL DEFAULT 0,
  `is_active` tinyint(1) NOT NULL DEFAULT 1,
  `approved_at` timestamp NULL DEFAULT NULL,
  `approved_by` bigint(20) unsigned DEFAULT NULL,
  `rejection_reason` text DEFAULT NULL,
  `view_count` int(11) NOT NULL DEFAULT 0,
  `like_count` int(11) NOT NULL DEFAULT 0,
  `download_count` int(11) NOT NULL DEFAULT 0,
  `purchase_count` int(11) NOT NULL DEFAULT 0,
  `rating_average` decimal(3,2) NOT NULL DEFAULT 0.00,
  `rating_count` int(11) NOT NULL DEFAULT 0,
  `display_order` int(11) NOT NULL DEFAULT 0,
  `featured_at` timestamp NULL DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
