INSERT INTO `page_seos` VALUES (9,'home','/','MechaMap - Cộng đồng Kỹ thuật Cơ khí Việt Nam','Nền tảng forum hàng đầu cho cộng đồng kỹ sư cơ khí Việt Nam. Thảo luận CAD/CAM, chia sẻ kinh nghiệm thiết kế, công nghệ chế tạo và giải pháp kỹ thuật.','mechanical engineering vietnam, CAD CAM, thiết kế cơ khí, forum kỹ thuật, cộng đồng kỹ sư','MechaMap - Cộng đồng Kỹ thuật Cơ khí Việt Nam','Tham gia cộng đồng kỹ sư cơ khí lớn nhất Việt Nam. Thảo luận về CAD/CAM, thiết kế máy móc, công nghệ chế tạo.',NULL,'MechaMap - Vietnam Mechanical Engineering Community','Join Vietnam\'s largest mechanical engineering community. Discuss CAD/CAM, machine design, manufacturing technology.',NULL,'/',0,NULL,1,'2025-07-08 03:05:43','2025-07-08 03:05:43'),(10,'forum.index','/forum','Forum Kỹ thuật - Thảo luận Cơ khí | MechaMap','Diễn đàn thảo luận kỹ thuật cơ khí. Trao đổi về thiết kế, vật liệu, gia công, tự động hóa và các chủ đề chuyên môn khác.','forum cơ khí, thảo luận kỹ thuật, thiết kế máy móc, vật liệu kỹ thuật, gia công CNC','Forum Kỹ thuật Cơ khí','Diễn đàn chuyên về kỹ thuật cơ khí với hàng nghìn chủ đề thảo luận từ các chuyên gia.',NULL,NULL,NULL,NULL,NULL,0,NULL,1,'2025-07-08 03:05:43','2025-07-08 03:05:43'),(11,'thread.show','/forum/threads/*','{thread_title} | Forum MechaMap','{thread_excerpt}','{thread_tags}, forum cơ khí, thảo luận kỹ thuật','{thread_title}','{thread_excerpt}',NULL,NULL,NULL,NULL,NULL,0,NULL,1,'2025-07-08 03:05:43','2025-07-08 03:05:43'),(12,'category.show','/forum/categories/*','{category_name} - Thảo luận Kỹ thuật | MechaMap','Các chủ đề thảo luận trong danh mục {category_name}. Tham gia cộng đồng kỹ sư cơ khí để học hỏi và chia sẻ kinh nghiệm.','{category_name}, forum cơ khí, thảo luận kỹ thuật, cộng đồng kỹ sư',NULL,NULL,NULL,NULL,NULL,NULL,NULL,0,NULL,1,'2025-07-08 03:05:43','2025-07-08 03:05:43'),(13,'forums.index','/forums','Diễn đàn Kỹ thuật Cơ khí - Cộng đồng MechaMap','Tham gia diễn đàn kỹ thuật cơ khí MechaMap với hàng nghìn chủ đề thảo luận về CAD/CAM, thiết kế máy móc, công nghệ chế tạo, vật liệu kỹ thuật và tự động hóa. Kết nối với cộng đồng kỹ sư chuyên nghiệp.','diễn đàn cơ khí, forum kỹ thuật, thảo luận CAD/CAM, thiết kế máy móc, công nghệ chế tạo, cộng đồng kỹ sư, mechanical engineering forum','Diễn đàn Kỹ thuật Cơ khí - MechaMap','Khám phá diễn đàn kỹ thuật cơ khí hàng đầu Việt Nam với hàng nghìn chủ đề về CAD/CAM, thiết kế, chế tạo và automation.','/images/seo/mechamap-forums-og.jpg','Mechanical Engineering Forum - MechaMap','Join Vietnam\'s leading mechanical engineering forum. Discuss CAD/CAM, machine design, manufacturing technology.','/images/seo/mechamap-forums-twitter.jpg','/forums',0,NULL,1,'2025-07-08 03:05:43','2025-07-08 03:05:43'),(14,'user.profile','/users/*','Hồ sơ {user_name} | MechaMap','Xem hồ sơ và hoạt động của {user_name} trong cộng đồng MechaMap.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,1,NULL,1,'2025-07-08 03:05:43','2025-07-08 03:05:43');
