INSERT INTO `documentations` (`id`, `title`, `slug`, `content`, `excerpt`, `category_id`, `author_id`, `reviewer_id`, `status`, `is_featured`, `is_public`, `allowed_roles`, `content_type`, `difficulty_level`, `estimated_read_time`, `meta_title`, `meta_description`, `meta_keywords`, `featured_image`, `view_count`, `rating_average`, `rating_count`, `download_count`, `sort_order`, `tags`, `related_docs`, `attachments`, `downloadable_files`, `published_at`, `reviewed_at`, `created_at`, `updated_at`) VALUES
	(5, 'Tổng quan về MechaMap', 'tong-quan-ve-mechamap', '# Tổng quan về MechaMap\n\n## Giới thiệu\nMechaMap là nền tảng cộng đồng kỹ thuật cơ khí hàng đầu Việt Nam, kết nối kỹ sư, nhà sản xuất và doanh nghiệp.\n\n## Tính năng chính\n- **Forum cộng đồng**: Thảo luận kỹ thuật và chia sẻ kiến thức\n- **Marketplace B2B2C**: Mua bán thiết bị và tài liệu kỹ thuật  \n- **Quản lý CAD**: Upload và chia sẻ bản vẽ kỹ thuật\n- **Hệ thống 8 cấp phân quyền**: Từ Guest đến Admin\n\n## Lợi ích\n- Kết nối với cộng đồng kỹ sư\n- Tiếp cận kiến thức chuyên môn\n- Tìm kiếm cơ hội kinh doanh\n- Chia sẻ và học hỏi kinh nghiệm', 'MechaMap là nền tảng cộng đồng kỹ thuật cơ khí hàng đầu Việt Nam.', 16, 65, NULL, 'published', 1, 1, NULL, 'guide', 'beginner', NULL, 'Tổng quan về MechaMap', 'Tìm hiểu về MechaMap, nền tảng kết nối cộng đồng kỹ sư cơ khí.', NULL, NULL, 2, 0.00, 0, 0, 1, '["mechamap","overview","introduction"]', NULL, NULL, NULL, '2025-07-01 02:10:14', NULL, '2025-07-01 02:10:14', '2025-07-01 02:18:15'),
	(6, 'Kiến trúc hệ thống MechaMap', 'kien-truc-he-thong-mechamap', '# Kiến trúc hệ thống MechaMap\n\n## Tổng quan\nMechaMap sử dụng kiến trúc MVC với Laravel framework.\n\n## Cấu trúc Database\n- **Core Tables**: users, categories, forums, threads\n- **Marketplace**: products, orders, payments\n- **Technical**: cad_files, materials, standards\n\n## API Structure\n- Authentication: JWT token-based\n- Endpoints: `/api/v1/*`\n- Rate limiting: Per user/IP\n\n## Security\n- Multi-factor authentication\n- Role-based permissions\n- Data encryption\n- XSS/CSRF protection', 'Tìm hiểu về kiến trúc kỹ thuật và các module chính của MechaMap.', 16, 65, NULL, 'published', 1, 1, NULL, 'reference', 'intermediate', NULL, 'Kiến trúc hệ thống MechaMap', 'Kiến trúc kỹ thuật của MechaMap: Laravel backend, database design.', NULL, NULL, 0, 0.00, 0, 0, 2, '["architecture","technical","laravel"]', NULL, NULL, NULL, '2025-07-01 02:10:14', NULL, '2025-07-01 02:10:14', '2025-07-01 02:10:14'),
	(7, 'Hướng dẫn quản trị hệ thống', 'huong-dan-quan-tri-he-thong', '# Hướng dẫn quản trị hệ thống\n\n## Dashboard\nAdmin dashboard cung cấp tổng quan về:\n- Thống kê người dùng\n- Hoạt động gần đây\n- Báo cáo doanh thu\n\n## User Management\n1. Xem danh sách: `/admin/users`\n2. Tạo user mới\n3. Phân quyền\n4. Khóa/mở khóa tài khoản\n\n## Content Management\n- Quản lý forum và categories\n- Kiểm duyệt bài viết\n- Xử lý báo cáo vi phạm\n\n## System Settings\n- Cấu hình chung\n- Security settings\n- Email và payment gateways', 'Hướng dẫn chi tiết cho Administrator về cách quản lý hệ thống.', 21, 65, NULL, 'published', 1, 0, '["admin"]', 'guide', 'advanced', NULL, 'Hướng dẫn quản trị hệ thống MechaMap', 'Hướng dẫn chi tiết cho Administrator về dashboard và user management.', NULL, NULL, 0, 0.00, 0, 0, 1, '["admin","management","dashboard"]', NULL, NULL, NULL, '2025-07-01 02:10:14', NULL, '2025-07-01 02:10:14', '2025-07-01 02:10:14'),
	(8, 'API Authentication Guide', 'api-authentication-guide', '# API Authentication Guide\n\n## Overview\nMechaMap API sử dụng JWT cho authentication.\n\n## Login\n```http\nPOST /api/v1/auth/login\n{\n    "email": "user@example.com",\n    "password": "password"\n}\n```\n\n## Using Token\n```http\nAuthorization: Bearer {token}\n```\n\n## Rate Limiting\n- Public: 60 requests/minute\n- Authenticated: 100 requests/minute\n- Admin: 200 requests/minute\n\n## Error Handling\n- 401: Unauthorized\n- 403: Forbidden\n- 429: Too Many Requests', 'Hướng dẫn xác thực và phân quyền API, sử dụng tokens.', 19, 65, NULL, 'published', 1, 0, '["admin","moderator","supplier","manufacturer"]', 'api', 'intermediate', NULL, 'MechaMap API Authentication Guide', 'Complete guide to MechaMap API authentication and authorization.', NULL, NULL, 0, 0.00, 0, 0, 1, '["api","authentication","security"]', NULL, NULL, NULL, '2025-07-01 02:10:14', NULL, '2025-07-01 02:10:14', '2025-07-01 02:10:14'),
	(9, 'Hướng dẫn sử dụng cho thành viên', 'huong-dan-su-dung-cho-thanh-vien', '# Hướng dẫn sử dụng cho thành viên\n\n## Đăng ký tài khoản\n1. Truy cập trang chủ\n2. Click "Đăng ký"\n3. Điền thông tin\n4. Xác nhận email\n\n## Sử dụng Forum\n- Chọn forum phù hợp\n- Tạo bài viết mới\n- Trả lời và thảo luận\n- Sử dụng tags\n\n## Marketplace\n- Tìm kiếm sản phẩm\n- Xem chi tiết và đánh giá\n- Liên hệ seller\n- Đặt hàng và thanh toán\n\n## Tips\n- Xây dựng reputation\n- Tham gia tích cực\n- Tuân thủ quy định', 'Hướng dẫn chi tiết cho thành viên về forum và marketplace.', 24, 65, NULL, 'published', 1, 1, NULL, 'tutorial', 'beginner', NULL, 'Hướng dẫn sử dụng MechaMap cho thành viên', 'Hướng dẫn chi tiết về cách tham gia forum và sử dụng marketplace.', NULL, NULL, 0, 0.00, 0, 0, 1, '["member","forum","marketplace"]', NULL, NULL, NULL, '2025-07-01 02:10:14', NULL, '2025-07-01 02:10:14', '2025-07-01 02:10:14'),
	(10, 'Test Documentation - Hướng dẫn kiểm tra hệ thống', 'test-documentation-huong-dan-kiem-tra-he-thong', '# Hướng dẫn kiểm tra hệ thống\r\n\r\n## 1. Giới thiệu\r\n\r\nTài liệu này hướng dẫn cách kiểm tra các tính năng của hệ thống MechaMap, đặc biệt là phần quản lý tài liệu.\r\n\r\n## 2. Các tính năng cần kiểm tra\r\n\r\n### 2.1. Tạo mới tài liệu\r\n- Điền đầy đủ thông tin\r\n- Kiểm tra validation\r\n- Kiểm tra lưu thành công\r\n\r\n### 2.2. Xem tài liệu\r\n- Kiểm tra hiển thị chi tiết\r\n- Kiểm tra định dạng nội dung\r\n\r\n### 2.3. Sửa tài liệu\r\n- Cập nhật thông tin\r\n- Kiểm tra lưu thành công\r\n\r\n### 2.4. Xóa tài liệu\r\n- Xác nhận xóa\r\n- Kiểm tra xóa thành công\r\n\r\n## 3. Kết quả mong đợi\r\n\r\nTất cả các tính năng hoạt động đúng, không có lỗi, và dữ liệu được lưu trữ chính xác.', 'Tài liệu test để kiểm tra tính năng CRUD của hệ thống documentation admin.', 21, 1, NULL, 'draft', 0, 1, NULL, 'guide', 'beginner', 2, NULL, NULL, NULL, NULL, 0, 0.00, 0, 0, 0, NULL, NULL, NULL, NULL, NULL, NULL, '2025-07-02 09:03:46', '2025-07-02 09:03:46');
