INSERT INTO `documentation_versions` (`id`, `documentation_id`, `user_id`, `version_number`, `content`, `change_summary`, `metadata`, `is_major_version`, `created_at`, `updated_at`) VALUES
	(3, 5, 65, '1.0.0', '# Tổng quan về MechaMap\n\n## Giới thiệu\nMechaMap là nền tảng cộng đồng kỹ thuật cơ khí hàng đầu Việt Nam, kết nối kỹ sư, nhà sản xuất và doanh nghiệp.\n\n## Tính năng chính\n- **Forum cộng đồng**: Thảo luận kỹ thuật và chia sẻ kiến thức\n- **Marketplace B2B2C**: Mua bán thiết bị và tài liệu kỹ thuật  \n- **Quản lý CAD**: Upload và chia sẻ bản vẽ kỹ thuật\n- **Hệ thống 8 cấp phân quyền**: Từ Guest đến Admin\n\n## Lợi ích\n- Kết nối với cộng đồng kỹ sư\n- Tiếp cận kiến thức chuyên môn\n- Tìm kiếm cơ hội kinh doanh\n- Chia sẻ và học hỏi kinh nghiệm', 'Tạo tài liệu ban đầu', NULL, 1, '2025-07-01 02:10:14', '2025-07-01 02:10:14'),
	(4, 6, 65, '1.0.0', '# Kiến trúc hệ thống MechaMap\n\n## Tổng quan\nMechaMap sử dụng kiến trúc MVC với Laravel framework.\n\n## Cấu trúc Database\n- **Core Tables**: users, categories, forums, threads\n- **Marketplace**: products, orders, payments\n- **Technical**: cad_files, materials, standards\n\n## API Structure\n- Authentication: JWT token-based\n- Endpoints: `/api/v1/*`\n- Rate limiting: Per user/IP\n\n## Security\n- Multi-factor authentication\n- Role-based permissions\n- Data encryption\n- XSS/CSRF protection', 'Tạo tài liệu ban đầu', NULL, 1, '2025-07-01 02:10:14', '2025-07-01 02:10:14'),
	(5, 7, 65, '1.0.0', '# Hướng dẫn quản trị hệ thống\n\n## Dashboard\nAdmin dashboard cung cấp tổng quan về:\n- Thống kê người dùng\n- Hoạt động gần đây\n- Báo cáo doanh thu\n\n## User Management\n1. Xem danh sách: `/admin/users`\n2. Tạo user mới\n3. Phân quyền\n4. Khóa/mở khóa tài khoản\n\n## Content Management\n- Quản lý forum và categories\n- Kiểm duyệt bài viết\n- Xử lý báo cáo vi phạm\n\n## System Settings\n- Cấu hình chung\n- Security settings\n- Email và payment gateways', 'Tạo tài liệu ban đầu', NULL, 1, '2025-07-01 02:10:14', '2025-07-01 02:10:14'),
	(6, 8, 65, '1.0.0', '# API Authentication Guide\n\n## Overview\nMechaMap API sử dụng JWT cho authentication.\n\n## Login\n```http\nPOST /api/v1/auth/login\n{\n    "email": "user@example.com",\n    "password": "password"\n}\n```\n\n## Using Token\n```http\nAuthorization: Bearer {token}\n```\n\n## Rate Limiting\n- Public: 60 requests/minute\n- Authenticated: 100 requests/minute\n- Admin: 200 requests/minute\n\n## Error Handling\n- 401: Unauthorized\n- 403: Forbidden\n- 429: Too Many Requests', 'Tạo tài liệu ban đầu', NULL, 1, '2025-07-01 02:10:14', '2025-07-01 02:10:14'),
	(7, 9, 65, '1.0.0', '# Hướng dẫn sử dụng cho thành viên\n\n## Đăng ký tài khoản\n1. Truy cập trang chủ\n2. Click "Đăng ký"\n3. Điền thông tin\n4. Xác nhận email\n\n## Sử dụng Forum\n- Chọn forum phù hợp\n- Tạo bài viết mới\n- Trả lời và thảo luận\n- Sử dụng tags\n\n## Marketplace\n- Tìm kiếm sản phẩm\n- Xem chi tiết và đánh giá\n- Liên hệ seller\n- Đặt hàng và thanh toán\n\n## Tips\n- Xây dựng reputation\n- Tham gia tích cực\n- Tuân thủ quy định', 'Tạo tài liệu ban đầu', NULL, 1, '2025-07-01 02:10:14', '2025-07-01 02:10:14');
