INSERT INTO `page_seos` (`id`, `route_name`, `url_pattern`, `title`, `description`, `keywords`, `og_title`, `og_description`, `og_image`, `twitter_title`, `twitter_description`, `twitter_image`, `canonical_url`, `no_index`, `extra_meta`, `is_active`, `created_at`, `updated_at`) VALUES
	(1, 'home', '/', 'MechaMap - Cộng đồng Kỹ thuật Cơ khí Việt Nam', 'Nền tảng forum hàng đầu cho cộng đồng kỹ sư cơ khí Việt Nam. Thảo luận CAD/CAM, chia sẻ kinh nghiệm thiết kế, công nghệ chế tạo và giải pháp kỹ thuật.', 'mechanical engineering vietnam, CAD CAM, thiết kế cơ khí, forum kỹ thuật, cộng đồng kỹ sư', 'MechaMap - Cộng đồng Kỹ thuật Cơ khí Việt Nam', 'Tham gia cộng đồng kỹ sư cơ khí lớn nhất Việt Nam. Thảo luận về CAD/CAM, thiết kế máy móc, công nghệ chế tạo.', '/images/seo/mechamap-home-og.jpg', 'MechaMap - Vietnam Mechanical Engineering Community', 'Join Vietnam\'s largest mechanical engineering community. Discuss CAD/CAM, machine design, manufacturing technology.', '/images/seo/mechamap-home-twitter.jpg', '/', 0, NULL, 1, '2025-06-25 10:57:14', '2025-06-25 10:57:14'),
	(2, 'forum.index', '/forum', 'Forum Kỹ thuật - Thảo luận Cơ khí | MechaMap', 'Diễn đàn thảo luận kỹ thuật cơ khí. Trao đổi về thiết kế, vật liệu, gia công, tự động hóa và các chủ đề chuyên môn khác.', 'forum cơ khí, thảo luận kỹ thuật, thiết kế máy móc, vật liệu kỹ thuật, gia công CNC', 'Forum Kỹ thuật Cơ khí', 'Diễn đàn chuyên về kỹ thuật cơ khí với hàng nghìn chủ đề thảo luận từ các chuyên gia.', '/images/seo/mechamap-forum-og.jpg', NULL, NULL, NULL, '/forum', 0, NULL, 1, '2025-06-25 10:57:14', '2025-06-25 10:57:14'),
	(3, 'thread.show', '/forum/threads/*', '{thread_title} | Forum MechaMap', '{thread_excerpt}', '{thread_tags}, forum cơ khí, thảo luận kỹ thuật', '{thread_title}', '{thread_excerpt}', '{thread_image}', NULL, NULL, NULL, '/forum/threads/{thread_id}', 0, NULL, 1, '2025-06-25 10:57:14', '2025-06-25 10:57:14'),
	(4, 'category.show', '/forum/categories/*', '{category_name} - Thảo luận Kỹ thuật | MechaMap', 'Các chủ đề thảo luận trong danh mục {category_name}. Tham gia cộng đồng kỹ sư cơ khí để học hỏi và chia sẻ kinh nghiệm.', '{category_name}, forum cơ khí, thảo luận kỹ thuật, cộng đồng kỹ sư', '{category_name} - Forum MechaMap', 'Thảo luận về {category_name} trong cộng đồng kỹ sư cơ khí Việt Nam.', NULL, NULL, NULL, NULL, '/forum/categories/{category_slug}', 0, NULL, 1, '2025-06-25 10:57:14', '2025-06-25 10:57:14'),
	(5, 'user.profile', '/users/*', 'Hồ sơ {user_name} | MechaMap', 'Xem hồ sơ và hoạt động của {user_name} trong cộng đồng MechaMap.', 'hồ sơ người dùng, {user_name}, cộng đồng kỹ sư', NULL, NULL, NULL, NULL, NULL, NULL, '/users/{user_id}', 1, NULL, 1, '2025-06-25 10:57:14', '2025-06-25 10:57:14'),
	(6, 'search.results', '/search*', 'Kết quả tìm kiếm | MechaMap', 'Tìm kiếm thông tin kỹ thuật, bài viết và thảo luận trong cộng đồng MechaMap.', 'tìm kiếm, forum cơ khí, kỹ thuật', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, NULL, 1, '2025-06-25 10:57:14', '2025-06-25 10:57:14'),
	(7, 'showcase.index', '/showcase', 'Showcase Dự án - Triển lãm Kỹ thuật | MechaMap', 'Khám phá những dự án kỹ thuật cơ khí ấn tượng từ cộng đồng. Thiết kế máy móc, sản phẩm và giải pháp công nghệ.', 'showcase dự án, triển lãm kỹ thuật, thiết kế cơ khí, sản phẩm công nghệ', 'Showcase Dự án Kỹ thuật - MechaMap', 'Khám phá và chia sẻ những dự án kỹ thuật cơ khí xuất sắc.', NULL, NULL, NULL, NULL, '/showcase', 0, NULL, 1, '2025-06-25 10:57:14', '2025-06-25 10:57:14'),
	(8, 'whats-new', '/whats-new', 'Có gì mới - Tin tức Kỹ thuật | MechaMap', 'Cập nhật tin tức mới nhất về công nghệ cơ khí, sản phẩm và xu hướng trong ngành.', 'tin tức kỹ thuật, công nghệ mới, xu hướng cơ khí, innovation', NULL, NULL, NULL, NULL, NULL, NULL, '/whats-new', 0, NULL, 1, '2025-06-25 10:57:14', '2025-06-25 10:57:14');
